css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("500px.com") {
  nav,
  nav .logo {
    height:32px!important;
  }
  nav .main li {
    line-height:32px!important;
  }
  nav .main ul {
    top: 32px!important;
  }
  nav .query, nav .user, nav .upload {
    margin-top:4px!important;
  }
  body.v3 .photo_show .photo_header {
    position:absolute;
    top:32px;
    width:100%;
    height:52px!important;
    background:transparent!important;
    transition:opacity 0.15s ease-out 0s;
  }
  body.v3 .photo_show .photo_header:hover {
    background:#222!important;
  }
  body.v3 .photo_show .photo_header .user_avatar.size_small img,
  body.v3 .photo_show .photo_header .user_avatar.size_small a.user_avatar_link,
  body.v3 .photo_show .photo_header .avatar
  {
    width:24px!important;
    height:24px!important;
    margin-top:1px!important;
    opacity:.8;
    overflow:hidden;
  }
  body.v3 .photo_show .photo_header:hover .user_avatar.size_small img,
  body.v3 .photo_show .photo_header:hover .user_avatar.size_small a.user_avatar_link,
  body.v3 .photo_show .photo_header:hover .avatar
  {
    width:50px!important;
    height:50px!important;
    margin-top:0!important;
    opacity:1;
  }
  body.v3 .photo_show .photo_header .left_side .user_avatar_wrap .membership_tag {
    width: 24px!important;
    font-size: 7px!important;
    font-weight: normal!important;
  }
  body.v3 .photo_show .photo_header:hover .left_side .user_avatar_wrap .membership_tag {
    width: 100%!important;
    font-size: 9px!important;
    font-weight: bold!important;
  }
  body.v3 .photo_show .photo_header .name_wrap .right_side .title {
    display:inline-block;
  }
  body.v3 .photo_show .photo_header:hover .name_wrap .right_side .title {
    display:block;
  }
  body.v3 .photo_show .photo_header .name_wrap .right_side .author_wrap {
    display:inline-block!important;
    margin:-1px 0 0 10px!important;
    float:right!important;
  }
  body.v3 .photo_show .photo_header:hover .name_wrap .right_side .author_wrap {
    display:block!important;
    margin:2px 0 0 0!important;
    float:none!important;
  }
  body.v3 .photo_show .photo_header .name_wrap .right_side .name {
    color:#999!important;
    font-size:8pt!important;
    font-weight:bold!important;
  }
  body.v3 .photo_show .photo_header .name_wrap .right_side .author_name a {
    color:#aaa!important;
    font-size:8pt!important;
  }
  body.v3 .photo_show .photo_header:hover .name_wrap .right_side .name {
    color:#F7F8FA!important;
    font-size:26px!important;
    font-weight:500!important;
  }
  body.v3 .photo_show .photo_header:hover .name_wrap .right_side .author_name a {
    color:#F7F8FA!important;
    font-size:14px!important;
  }
  body.v3 .photo_show .photo_header .name_wrap .right_side .author_follow_button {
    display:none;
  }
  body.v3 .photo_show .photo_header:hover .name_wrap .right_side .author_follow_button {
    display:block;
  }
  body.v3 .photo_show .photo_header .actions {
    opacity:0;
    display:none!important;
  }
  body.v3 .photo_show .photo_header:hover .actions {
    opacity:1;
    display:block!important;
  }
  body.v3 .photo_show .photo_header .actions .stats  {
    margin:0 5px 0!important;
  }
  body.v3 .photo_show .photo_header .actions .stats .data_wrap {
    padding:0 15px !important;
  }
  body.v3 .photo_show .photo_header .actions .stats .drop {
    top:50px!important;
  }
  body.v3 .photo_show .photo_header .actions .sharelizers,
  body.v3 .photo_show .photo_header .actions .like_favorite {
    margin-top:0!important;
  }
  body.v3 .photo_show .photo_header .name_wrap {
    padding-top:0!important;
  }
}
