css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.mousehuntgame.com/inventory.php"), url-prefix("https://www.mousehuntgame.com/inventory.php"){
.inventoryitemview.convertibles .itembox {
	margin: 0px !important;
	border-color: rgb(204, 204, 204) !important;
	width: 69px !important;
	height: 96px !important;
	float:left !important;
}
/* 5: 93 110 -8: 69 96  -10: 55 85*/
.inventoryitemview.convertibles .itembox .rightcol,
.inventoryitemview.convertibles .itembox .convertibledetails {
	display:none;
}
.inventoryitemview.convertibles .itembox .itemname {
	font-size: 1em !important;
	font-weight: normal !important;
	overflow: hidden;
	white-space: nowrap;
	height: 1.2em;
	text-overflow: ellipsis;
}
.inventoryitemview.convertibles .itembox .leftcol .itemImage {
	margin: 0 !important;
	width: 100% !important;
	height: 100% !important;
}
.inventoryitemview.convertibles .itembox .leftcol .itemImage img {
	width:100%;
	height:100%;
}
.inventoryitemview.convertibles .larryLexicon {
	position: absolute;
	right: 1px;
	top: 1px;
}



.inventoryitemview.bait .itembox,
.inventoryitemview.weapons .itembox,
.inventoryitemview.bases .itembox,
.inventoryitemview.trinket .itembox,
.inventoryitemview.skins .itembox,
.inventoryitemview.breakable .itembox {
	margin: 0px !important;
	width: 105px !important;
	float: left !important;
}
.inventoryitemview.bait .itembox .rightcol,
.inventoryitemview.weapons .itembox .rightcol,
.inventoryitemview.bases .itembox .rightcol,
.inventoryitemview.trinket .itembox .rightcol,
.inventoryitemview.skins .itembox .rightcol,
.inventoryitemview.breakable .itembox .rightcol {
	display:none;
}
.inventoryitemview.bait .itembox .itemname,
.inventoryitemview.weapons .itembox .itemname,
.inventoryitemview.bases .itembox .itemname,
.inventoryitemview.trinket .itembox .itemname,
.inventoryitemview.skins .itembox .itemname,
.inventoryitemview.breakable .itembox .itemname{
	font-size: 1em !important;
	font-weight: normal !important;
	overflow: hidden;
	white-space: nowrap;
	height: 1.2em;
	text-overflow: ellipsis;
}

}
