css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* By Bob Toomey, July 26, 2008.  An extension of Lou Cypher's Colorize Firefox 2 Active Tab. Colors the active tab, the inactive tabs, the hover color and the font style.  Change the colors and styles to suit yourself. */

.tabbrowser-tab[selected="true"],
.tabs-bottom,
[class="close-button tabs-closebutton"] {
  background-color: rgb(255, 0, 0) !important; font-weight: 900 !important;  
}

.tabbrowser-tab[selected="true"] .tab-text {
  color: black !important; 
  -moz-opacity: 1 !important;
}


.tabbrowser-tab:not([selected="true"]) {
  background-color: rgb(0, 0, 255) !important; font-weight: 900 !important;  
} 

.tabbrowser-tab:not([selected="true"]) .tab-text {
  color: white !important; 
  -moz-opacity: 1 !important;
}

.tabbrowser-tab:not([selected="true"]):hover {
background-color: rgb(0, 255, 0) !important;
} 

.tabbrowser-tab:not([selected="true"]):hover .tab-text {
  color: black !important;
  -moz-opacity: 1 !important;
}
