css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("reddit.com"){

	body.with-listing-chooser .listing-chooser.initialized .grippy:before{
	    background-color: hsla(45, 80%, 70%, 0.7) !important;
	}
	
	body.with-listing-chooser .listing-chooser.initialized .grippy:hover:before{
	    background-color: hsla(45, 80%, 70%, 1.0) !important;
	}
	
	body.with-listing-chooser.listing-chooser-collapsed .listing-chooser.initialized .grippy:before{
	    background-color: hsla(240, 80%, 80%, 0.5) !important;
	}
	
	body.with-listing-chooser.listing-chooser-collapsed .listing-chooser.initialized .grippy:hover:before{
	    background-color: hsla(240, 80%, 80%, 0.75) !important;
	}
}
