css:
@-moz-document domain("iqdb.org") {
body{
  font-family: "Microsoft Yahei";
  text-align:center;
  background:url(http://ntu.me/di/MXPV/299526_1369647353_7076.jpg) no-repeat fixed top;
  color:#fff;
  opacity:0.9;
}

*{
  margin-left:auto;
  margin-right:auto;
}

a:link {
  color:#3f3f3f;
  text-decoration:none!important;
}

a:visited {
  color:#3f3f3f;
  text-decoration:none!important;
}

a:hover {
text-shadow:0 2px 2px rgba(0,0,0,.2)!important;
}

p.flow{
  display:none;
}
ul,div.err{
  margin-left:auto;
  margin-right:auto;
  width:500;
  list-style-type: circle;
  border: 1px solid #558ed5;
  background:#558ed5!important;
  color:#f5f5f5;
  border-radius: 15px;
}


input+table.form{

  background:#558ed5!important;
  color:#f5f5f5;
  opacity:0.6;
  border-radius: 15px;
}

input+table.form th{
  width:500;
}

input+table.form:hover{
  opacity:1;
}

input{
  opacity:0.6;
}

input:hover{
  opacity:1;
}

div.flow{
  
}

input#url{
  border: 1px solid #b6b6b6;
  width: 418px;
  height: 30px;
  font-size:15px;
}

input[type="submit"],input[type="file"]{
  border: 1px solid #558ed5;
  background:#558ed5!important;
  color:#f5f5f5!important;
  font-family:"Microsoft Yahei",sans-serif!important;
  font-size:15px;
}

input[type="submit"]{
  position:relative;
  left:550;
  top:-35;
  width: 80px;
  height: 30px;
}


input[type="submit"]:hover,input[type="file"]:hover{
	box-shadow: 0 2px 2px rgba(0,0,0,.2);
}
input[type="checkbox"]{
  border: 10px solid #b6b6b6;
}

div#pages div table{
  border: 1px solid #558ed5;
  background:#558ed5!important;
  color:#f5f5f5;
  border-radius: 15px;
  margin:10px;
}
}
