/*
 * Author:            Will (war59312@gmail.com)
 * Description:       Article only for maximumpc.com. Removes all the junk from articles. A lot easier to read.
*/

/* +++ changelog +++
March 24, 2014
- Hides "Related Articles"
July 25, 2013
- original release
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("maximumpc.com") {

/* Hides "Header" */
#masthead { display: none !important; }

/* Hides The "SideBar" */
#sidebar-first { display: none !important; }

/* Hides "Newsletter Signup" */
#-future-adestra-article-signup-form { display: none !important; }

/* Hides "Comments" */
.comments_wrapper_header, #comments, #box { display: none !important; }

/* Hides "User Login Box" */
#user-login-form { display: none !important; }

/* Hides "Related Articles" */
div[class*="related-articles"] { display: none !important; }

/* Hides "Footer" */
#footer { display: none !important; }

}
