css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(deviantart.com) {
#searchArea.tang #searchCap {
    background:url(http://img337.imageshack.us/img337/9317/sleekpinksl3.gif) no-repeat 0px -199px !important;
}
#searchArea.tang #searchDropdown {
    color: #000000 !important;
    background:url(http://img337.imageshack.us/img337/9317/sleekpinksl3.gif) no-repeat 0px -222px !important;
}
#searchArea.tang #searchDropdown:hover {
    background:url(http://img337.imageshack.us/img337/9317/sleekpinksl3.gif) no-repeat 0px -268px !important;
}
#searchArea.tang #searchButton {
    background:url(http://img337.imageshack.us/img337/9317/sleekpinksl3.gif) no-repeat -138px -222px !important;
}
#searchArea.tang #searchButton:hover {
    background:url(http://img337.imageshack.us/img337/9317/sleekpinksl3.gif) no-repeat -138px -268px !important;
}
#noTouch #searchDropdownOverlay.tang {
    background:url(http://img337.imageshack.us/img337/9317/sleekpinksl3.gif) no-repeat -1px -246px !important;
    border-bottom: 1px solid #640A40 !important;
}
#noTouch #searchDropdownMenu.tang {
    background: #ED20C2 url(http://img520.imageshack.us/img520/6199/searchdropdownmenubgpinen6.gif) bottom right no-repeat !important;
    border: 1px solid #640A40 !important;
    border-top: 0px !important;
}
#noTouch #searchDropdownMenu.tang div.sdmItem {
    color: #000000 !important;
}
#noTouch #searchDropdownMenu.tang div.sdmItemHover {
    background-color: #BF5F80 !important;
    color: #000000 !important;
}
}
