css:
@-moz-document url-prefix('http://www.alienaa.com/index.php?p='), url-prefix('http://alienaa.com/index.php?p=') {
    body {
        background-color: #000000 !important;
        color: #808080 !important;
    }
    
    a {
        color: #CED9E0 !important;
    }
    
    a:hover {
        color: #CED9E0 !important;
        text-decoration: underline !important;
    }
    
    a[href="#"] {
        color: #CED9E0 !important;
        text-decoration: line-through !important;
    }
    
    .bluebox, #content h2 {
        background-color: #212121 !important;
    }
    
    .greybox {
        background-color: #111111 !important;
    }
    
    .lbluebox, #content h4 {
        background-color: #111111 !important;
        border-radius: 5px 5px !important;
    }
    
    .mbmoderate:hover {
        background-color: #111111 !important;
    }
    
    .progress {
        background-color: #B70210 !important;
        border: 1px solid #212121 !important;
    }
    
    .pvalue {
        background-color: #029111 !important;
    }
    
    .ptext {
        color: #FFFFFF !important;
        text-shadow: 1px 1px #000000 !important;
    }
    
    .username {
        border-bottom: 1px solid #808080 !important;
    }
    
    .inventorytable td > div {
        border: 2px solid #212121 !important;
    }
    
    .equipped {
        background-color: #111111 !important;
    }
    
    .altrows tbody tr:nth-child(2n) {
        background-color: #111111 !important;
    }
    
    .highlight {
        background-color: #212121 !important;
        border: 2px solid #808080 !important;
    }
    
    div.menubox .annex {
        border: 2px solid #808080 !important;
        background-color: #111111 !important;
        margin: -1px 4px 0px 0px !important;
    }
    
    .galaxygym .radio {
        border: 2px solid #212121 !important;
        background-color: #111111 !important;
    }
    
    .galaxygym .radio:hover {
        border: 2px solid #808080 !important;
        background-color: #111111 !important;
    }
    .galaxygym .radio.selected {
        border: 2px solid #808080 !important;
        background: #212121 !important;
    }
    
    .herbgarden .radio {
        border: 2px solid #212121 !important;
    }
    
    .herbgarden .radio.selected {
        background-color: #111111 !important;
    }
    
    .giftshop .check {
        border: 2px solid #212121 !important;
    }
    
    .giftshop .check.selected{
        background-color: #111111 !important;
    }
        
    input, select, textarea, a.btn, a.btn-large {
        background-color: #B9B9B9 !important;
        color: #000000 !important;
    }
    
    textarea {
        font-family: Verdana, Arial, Tahoma !important;
        font-size: 11px !important;
    }
    
    .menubox .menu_group li:hover {
        background-color: #111111 !important;
    }
    
    .tab_group {
        background-color: #212121 !important;
        border-radius: 5px !important;
        border: 0;
    }
        
    .tabs li a {
        background-color: #111111 !important;
        border: 2px solid #111111 !important;
        border-radius: 5px 5px 0 0 !important;
        margin-left: 8px !important;
    }
    
    .tabs li a.active {
        background-color: #212121 !important;
        border: 2px solid #212121 !important;
    }
    
    .ui-tooltip {
        background-color: #111111 !important;
    }
    
    .mbboard img {
        background: url(http://i.imgur.com/32L3hE4.png) no-repeat !important;
    }
    
    .mbboard td {
        border: 0 !important;
    }
    
    tr.mbboard:nth-child(odd) {
        background-color: #212121 !important;
    }
    
    tr.mbboard:nth-child(even) {
        background-color: #111111 !important;
    }
}
