css:
@-moz-document domain("www.tipidpc.com") {
body, h1, h3, label, a, a:visited, p.caption, u, p, td{
  color: #A6A6A6 !important;
}

#nav, #nav a, #layout, #banner_big, #buddies, #announcement, div.normalbox, body.window 

#messageinfo , body.window #messagetools{
  background: #101010 !important;
  color: #A6A6A6 !important;
}

table.pager, .inboxlist thead th, body.window table.pmtools{
  background: #101010 !important;
  color: #A6A6A6 !important;
  border: 1px solid #101010 !important;
}

.window, DIV.regbutton, body.window thead td{
  background: #323232 !important;
  border: 1px solid #505050 !important;
}

.odd, .even, .odd td, .even td, textarea, input.text, select, input{
  background: #404040 !important;
  color: #A6A6A6 !important;
  border: 1px solid #404040 !important;
}


.flagged td, .premium, div.alertbox, .new td{
  background: #606060 !important;
  color: #A6A6A6 !important;
  border: 2px solid #606060 !important;
}

h3.wintitle{
  background: #101010 !important;
  border-bottom: 2px solid #303030 !important;
}

p.usermeta, p.itemmeta, ul.posts li{
  background: #454545 !important;
  color: #A6A6A6 !important;
  border: 1px solid #303030 !important;
}  

div.postcontent{
  background: #323232 !important;
  color: #A6A6A6 !important;
  border: 1px solid #404040 !important;
}  

blockquote
{
  background: #606060 !important;
  color: #B6B6B6 !important;
  border: 1px solid #707070 !important;
}  
  
.red{
    color: #E27070 !important;
 }
}
