css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

#searchArea.tang #searchCap {
    background:url(http://img512.imageshack.us/img512/378/sleekrx3.gif) no-repeat 0px -199px !important;
}
#searchArea.tang input.sleekSearchInput {
    background:url(http://img512.imageshack.us/img512/378/sleekrx3.gif) repeat-x 0px -291px !important;
}
#searchArea.tang #searchDropdown {
    background:url(http://img512.imageshack.us/img512/378/sleekrx3.gif) no-repeat 0px -222px !important;
}
#searchArea.tang #searchDropdown:hover {
    background:url(http://img512.imageshack.us/img512/378/sleekrx3.gif) no-repeat 0px -268px !important;
}
#searchArea.tang #searchButton {
    background:url(http://img512.imageshack.us/img512/378/sleekrx3.gif) no-repeat -138px -222px !important;
}
#searchButton:hover {
    background:url(http://img512.imageshack.us/img512/378/sleekrx3.gif) no-repeat -138px -268px !important;
}
#searchArea.tang #searchButton:hover {
    background:url(http://img512.imageshack.us/img512/378/sleekrx3.gif) no-repeat -138px -268px !important;
}
#noTouch #searchDropdownOverlay.tang {
    background:url(http://img512.imageshack.us/img512/378/sleekrx3.gif) no-repeat -1px -246px !important;
    border-bottom: 1px solid #576960 !important;
}
#noTouch #searchDropdownMenu.tang {
    background:#829586 url(http://img186.imageshack.us/img186/6749/searchdropdownmenubgtankr4.gif) bottom right no-repeat !important;
    border: 1px solid #405249 !important;
    border-top: 0px !important;
}
#noTouch #searchDropdownMenu.tang div.sdmItemHover {
    background-color: #9cac9e !important;
    color: #384641 !important;
}



}
