css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("coursera.org")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer, fieldset, .course-lecture-quiz-status-div, #header

{
color: white !important;
background: black !important;
}

select, pre, .btn, .coursera-body, .coursera-header, .coursera-footer, .coursera-full-canvas, .coursera-page, #origami, .coursera-course-listing-box-wide, .modal-header, .modal-body, .coursera-course-modal-invite-friends-social, .course-quiz-container, .QL_overlay, .QL_scale, #course-page-content, .course-topbanner, .coursera, #globalWrapper, #side, #content, #nav, .course-page-sidebar div, .row-fluid, .course-forum-header, .modal-footer, .coursera-course-listing-box, .container-fluid, #column-content, #nav:after, .course-forum-post-header, .course-forum-thread-header, .course-quiz-question-body, .course-quiz-question-explanation, .course-quiz-preamble, .course-forum-profile-badge, .course-quiz-honor-code, .course-quiz-submit-button-container, .alert, .coursera-wysihtml5-toolbar div, .coursera-wysihtml5-toolbar-modes button, .coursera-wysihtml5-commands button, .course-forum-permalink, .modal, .course-signaturetrack-topbanner, .course-signaturetrack-topbanner span, .hg-asst-header, .navbar-inner, .hg-nav-top-row, .assessmentExposition, .assessmentAnswer, .contentToolbar

{
background: black !important;
}

a

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, input, code, code pre

{
background: #333333 !important;
}

/* green */
.course-quiz-honor-code-thanks

{
color: #00ff00 !important;
}

}
