css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

#searchArea.tang #searchCap {
    background:url(http://img157.imageshack.us/img157/9279/sleekpinkme6.gif) no-repeat 0px -199px !important;
}
#searchArea.tang input.sleekSearchInput {
    background:url(http://img157.imageshack.us/img157/9279/sleekpinkme6.gif) repeat-x 0px -291px !important;
}
#searchArea.tang #searchDropdown {
    background:url(http://img157.imageshack.us/img157/9279/sleekpinkme6.gif) no-repeat 0px -222px !important;
}
#searchArea.tang #searchDropdown:hover {
    background:url(http://img157.imageshack.us/img157/9279/sleekpinkme6.gif) no-repeat 0px -268px !important;
}
#searchArea.tang #searchButton {
    background:url(http://img157.imageshack.us/img157/9279/sleekpinkme6.gif) no-repeat -138px -222px !important;
}
#searchButton:hover {
    background:url(http://img157.imageshack.us/img157/9279/sleekpinkme6.gif) no-repeat -138px -268px !important;
}
#searchArea.tang #searchButton:hover {
    background:url(http://img157.imageshack.us/img157/9279/sleekpinkme6.gif) no-repeat -138px -268px !important;
}
#noTouch #searchDropdownOverlay.tang {
    background:url(http://img157.imageshack.us/img157/9279/sleekpinkme6.gif) no-repeat -1px -246px !important;
    border-bottom: 1px solid #771a77 !important;
}
#noTouch #searchDropdownMenu.tang {
    background:#d640d6 url(http://img157.imageshack.us/img157/5981/sleek2pinkcn0.gif) bottom right no-repeat !important;
    border: 1px solid #701870 !important;
    border-top: 0px !important;
}
#noTouch #searchDropdownMenu.tang div.sdmItemHover {
    background-color: #f066f0 !important;
    color: #701870 !important;
}



}
