@-moz-document url-prefix("http://h.hatena.ne.jp/keyword/") {
 h2.title>a{display:none}
}
@-moz-document domain("h.hatena.ne.jp") {
 *{color: #444 !important }
 *{background-color: white !important}
 .hatena-star-highlight{background-color:#FF0 !important}
