@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain('tumblr.com') {







/*CURRENT ICONS INVISIBLE begin*/



img[src*="http://24.media.tumblr.com/884b10cde4f75d3238a588d96fc7d3c3/tumblr_mr3t8fCabz1r4hssfo1_500.png"],



.new_post_label_icon {



    opacity: 0 !important;



}



/*CURRENT ICONS INVISIBLE end*/







/*NEW ICON BAR GRAPHIC begin*/



#new_post_label_text,



#new_post_label_photo,



#new_post_label_quote,



#new_post_label_link,



#new_post_label_chat,



#new_post_label_audio,



#new_post_label_video {



  background: rgba(255,255,255, 1.0) url("http://24.media.tumblr.com/884b10cde4f75d3238a588d96fc7d3c3/tumblr_mr3t8fCabz1r4hssfo1_500.png") no-repeat !important;



}



/*NEW ICON BAR GRAPHIC end*/



/*REPLACE URL ABOVE - Dimensions 513px by 95px for standard icon bar



                                                      513px by 190px for icon bar with effects*/











/*ICON POSITIONING begin*/



#new_post_label_text {



  background-position: 2px 0px !important;



  border-top-left-radius: 8px !important;



  border-bottom-left-radius: 8px !important;



}



#new_post_label_photo {



  background-position: -73px 0px !important;



}



#new_post_label_quote {



  background-position: -148px 0px !important;



}



#new_post_label_link {



  background-position: -223px 0px !important;



}



#new_post_label_chat {



  background-position: -298px 0px !important;



}



#new_post_label_audio {



  background-position: -373px 0px !important;



}



#new_post_label_video {



  background-position: -448px 0px !important;



  border-top-right-radius: 8px !important;



  border-bottom-right-radius: 8px !important;



}



/*ICON POSITIONING end*/







/*TEXT LABELS and HOVER EFFECTS begin*/



/*ICON TEXT LABELS begin*/



#new_post_label_text:hover,



#new_post_label_photo:hover,



#new_post_label_quote:hover,



#new_post_label_link:hover,



#new_post_label_chat:hover,



#new_post_label_audio:hover,



#new_post_label_video:hover {



  background-color: rgba(0,0,0, .05) !important;



  color: black !important;



  font-weight: bold !important;



}



 /*ICON TEXT LABELS end*/







/*ICON EFFECTS HOVER begin*/



#new_post_label_text:hover {



  background-position: 2px -95px !important;



}



#new_post_label_photo:hover {



  background-position: -73px -95px !important;



}



#new_post_label_quote:hover {



  background-position: -148px -95px !important;



}



#new_post_label_link:hover {



  background-position: -223px -95px !important;



}



#new_post_label_chat:hover {



  background-position: -298px -95px !important;



}



#new_post_label_audio:hover {



  background-position: -373px -95px !important;



}



#new_post_label_video:hover {



  background-position: -448px -95px !important;



}



 /*ICON EFFECTS HOVER end*/



/*TEXT LABELS and HOVER EFFECTS end*/







} /*REQUIRED BRACKET - do NOT delete*/
