@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("play-asia.com") 
{

body, .drpshdw, #maincontent, .lb, .noli, .rb,
 .gb, .tl, .greybox, .catlinks, .box3, .thumbnails  
{
background:#000 !important;
box-shadow:none !important;
}
a, li, p, h1, .drpshdw, #footerlinks, .greybox,
 .datatab, .rb, .lb, .gb, .gb2, .cb, span, div 
{
color:#FFF !important;
box-shadow:none !important;
}
#social, .__autocomplete, tr, td 
{
color:#FFF !important;
background:#000 !important;
box-shadow:none !important;
}
.htab, .htabu, .pf_container
{
border:none !important;
background:#000 !important;
box-shadow:none !important;
}
.cartb, .stype, .cobut, .smallbut, .rbut, 
.bh, #pf_main_container li:hover
{
color:#FFF !important;
border:none !important;
background:red !important;
box-shadow:none !important;
}
.txtsmall_green {
border:none !important;
background:green !important;
box-shadow:none !important;
}
#suggestions0, #suggestions1, #suggestions2, #suggestions3,
#suggestions4, #suggestions5, #suggestions6, #suggestions7,
#suggestions8, #suggestions9, #suggestions10, #suggestionspreview {
background:#000 !important;
}
div {
background:#000 !important;
}
}
