css:
@-moz-document domain(fanfiction.net)
{
	div#filters.modal{ position:inherit !important; width:inherit !important; margin-left:inherit !important; } /* Moves the Filters box to the right place on the page. */
	div#filters.hide { display:block !important; } /* Makes the Filters box visible. */
	div#content_wrapper_inner span.btn {display:none !important;} /* Hides all buttons in the content wrapper. (This is to get rid of the Filters and Cancel buttons.  */
	div#content_wrapper_inner div.modal-footer .btn.btn-primary {display:inline-block !important;} /* Makes the Apply button visible again. */

	/* This bit forces the cursor to use the normal browser cursors when hovering the pointer over the stories in the browser page. */
	div.z-list.zhover.zpointer {cursor:auto !important;}
}
