css:
@-moz-document domain("www.php.net") {
    /* Nav */
    
    #headmenu {
        width: 100% !important;
        margin: 0 !important;
    }
    
    /* Docs */

    .docs p.verinfo {
        float: none !important;
        margin: 5px 0 0 0 !important;
        color: grey !important;
    }   
    .docs h1.refname {
        float: none !important;
        color: #484484 !important;
        font-size: 2em !important;
        padding: 10px 0 0 0 !important;
        margin: 0 !important;
    }
    
    .docs p.refpurpose {
        border-top: 2px solid #444444 !important;
        background: #EEE !important;
        padding: 10px 5px !important;
    }
    
    .docs span.refname {
        font-weight: bold;
    }
    
    .docs .fieldsynopsis .type, 
    .docs .methodsynopsis .type, 
    .docs .constructorsynopsis .type {
        color: #8B86AD !important;
    }
    
    .docs .parameter {
        color: #333167 !important;
    }
}
