css:
@-moz-document domain("himarin.net") {

  #header-right, #header-menu, .himajin-headline, #footer,
  *[class*="popularArticles"], #right-contents, .article-date,
  .bookmark, .article-header, iframe, *[href*="accesstrade"],
  *[src*="amazon"], *[id*="zenback"], .pager, #comment-form
  { display: none !important; }

  html, body { background-image: none; }
  #main, #main-container, #header-container { width: 750px; }

  .article-body dt { font-size: 12px; }
  .article-area, .article-option {
    border: 0;width: 710px;
    box-shadow: 0 1px 4px 2px rgba(141,197,238,.2);
    padding: 20px;
  }

}
