css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rapidgator.net") {

#left_banner, #right_banner, #top_banner {
    display: none;
}

div[class="table-download"] tbody tr th:nth-child(3) {
    display: none;
}

div[class="table-download"] tbody tr th:nth-child(1) {
    display: none;
}

div[class="table-download"] tbody tr td, a[id="copy"] {
    display: none;
}

div[class="text-block"] div[class="btm"] p[align="center"] {
    display:none;
}

}
