css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("endoftheamericandream.com") {

@font-face {
  font-family: 'Keania One';
  font-style: normal;
  font-weight: 400;
  src: local('Keania One'), local('KeaniaOne-Regular'), url(https://themes.googleusercontent.com/static/fonts/keaniaone/v1/Xs0xx5kHUREmTXw6nnDuCr3hpw3pgy2gAi-Ip7WPMi0.woff) format('woff');
}
div.post-headline h1 a, div.post-headline h2 a,
div.post-headline h1, div.post-headline h2,
h1.blogtitle a, h2.blogtitle a, p.tagline {
    font-family: "Keania One"  !important;
    font-weight: 400 !important;
    }
.post-headline h2 a:hover {
    color: #01bd01 !important;
    }
div.post-headline h1, div.post-headline h2 {
    font-size: 2.5em !important;
    }
h1.blogtitle a, h2.blogtitle a {
    font-size: 1.2em !important;
    }
p.tagline {
    font-size: 1.0em !important;
    }
#right, #left, .sociable, .navigation-middle,
 #disqus_thread, #footer {
    display: none !important;
    }
.post, .navigation-bottom {
    width: 700px !important;
    }
.post p {
    font-size: 1.8em !important;
    line-height: 1.3 !important;
    color: #444444 !important;
    }
p a {
    color: #0018bd !important;
    border-bottom: 2px dotted #0018bd !important;
    }
a {
    transition-property:  color, border-bottom !important;
    transition-duration:  .25s, .25s !important;
    transition-timing-function: ease-out !important;
    }
p a:hover {
    text-decoration: none !important;
    color: #01bd01 !important;
    border-bottom: 2px dotted #01bd01 !important;
    }

li.cat-item a:hover {
    color: red !important;
    }
}
