css:
@-moz-document domain('xkcd.com') {

body{
  background-color:#96A8C8;
}
#bottom{
  display:none;
}
#middleContainer{
  color:#fff;
}
#middleContainer #ctitle{
  color:#000;
}

footer{
  display:none;
}
#news{
  display:none;
}
#header-wrapper, #entry-wrapper{
  border-top-color:rgb(7, 20, 25);
  border-top-width:1px;
  border-top-style:solid;
  border-left-color:rgb(7, 20, 25);
  border-left-width:1px;
  border-left-style:solid;
  border-right-color:rgb(7, 20, 25);
  border-right-width:1px;
  border-right-style:solid;
  border-bottom-color:rgb(7, 20, 25);
  border-bottom-width:1px;
  border-bottom-style:solid;
}
#header-wrapper{
  padding-bottom:32px;
}
.main-nav{
  float:right;
  margin-top:8px;
}
.entry+.main-nav{
  margin-top:-66px;
}

}
