css:
@-moz-document 
/* Blogger Frasi Team (info@blogger-fa.com) */
/* http://www.blogger-fa.com/ */

url-prefix(http://sites.google.com/a/blogger-fa.com/wiki),
url-prefix(https://sites.google.com/a/blogger-fa.com/wiki),
url-prefix(http://wiki.blogger-fa.com/) {

h3#goog-ws-page-title-header {
font: bold 1.4em Times New Roman;
}
h2 {
margin: 0px !important;
font: bold 1.2em Times New Roman;
}

#col0, h2, h3#goog-ws-page-title-header, input#jot-ui-searchInput, #jot-content0, input.editable  {
direction: rtl;
text-align: right;
}

textarea.goog-ws-comment-textarea {
direction: rtl;
text-align: right;
padding: 2px 3px;
line-height: 18px;
}

.goog-ws-comment {
border: 1px solid #7f9db9;
padding: 2px 3px;
margin:2px 191px 2px 0px;
}
.goog-ws-comment p {
direction: rtl;
text-align: right !important;
font: normal 8pt Tahoma !important;
line-height: 18px;
}


}
