css:
@-moz-document domain("flightcar.com") {
html {
  padding-top: 30px;
}
body:before {
  background: #e40000;
  border-bottom: 1px solid #c90000;
  box-shadow: inset 0 1px 0 #fc0000;
  color: #fff;
  content: 'You are on production. Be careful, my son.';
  display: block;
  height: 30px;
  line-height: 30px;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  text-align: center;
  text-shadow: none;
  z-index: 1000000;
}
.navbar-fixed-top {
  top: 30px !important;
}
.subnav {
  top: 71px;
}
@media (max-width: 767px) {
.navbar-user .logo-text {
top: 30px !important;
}
}
}
