css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.youtube.com/feed/subscriptions") {

    div#content
    {
        min-width: 100%;
        min-height: 100%;
    }
    
    div#footer-container
    {
        min-width: 100%;
    }


    #page{
    min-width:98% !important;
    }
    
    #guide-container:not(:hover){
        opacity: 0.3 !important;
    }

    .branded-page-v2-primary-col {
        overflow:hidden;
        min-height: 500px;
        min-width: 100px;
    }    

    .feed-page > ul > li {
        width: 500px; 
        float: left;
    }
    
    div.feed-item-header {
        display: none;
    }
    
    li.feed-item-container {
        float: left;
        width: 500px;
    }

    .feed-author-bubble {
    margin: 2px 2px 0 0 !important;
    width: 39px !important;
    

    }

    div.feed-load-more-container {
        width: 500px;
        padding: 10px;
        height: 150px;
        float: right;
        vertical-align:50%;
        }

    .yt-thumb-185 {
        width: 170px !important;
        height: auto;
        overflow: hidden;
    }

    img[data-group-key="thumb-group-0"]{
        max-width: 170px;
    }
    
    a.watched 
    {
        display:none !important;
    }
    
}
