css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("crocko.com") {
html, body, #footer, #lang {
background:#000 !important;
}
a, h1, h2, h3, h4, p, li {
color:#FFF !important;
}
.free, .premium, .tab, .cur, .web-upload, 
td, th, div, .txt, ul, ul a:hover {
color:#FFF !important;
background:#000 !important;
}
}
