css:
@-moz-document url("http://www.tumblr.com/dashboard"), url("http://www.tumblr.com/inbox"), url("https://www.tumblr.com/settings"), url("https://www.tumblr.com/register"), url("http://www.tumblr.com/new/blog"), url("http://www.tumblr.com/help"), url("https://www.tumblr.com/preferences"), url("https://www.tumblr.com/lookup"), url("http://www.tumblr.com/explore"), url("http://www.tumblr.com/following"), url("http://www.tumblr.com/likes"), url("https://www.tumblr.com/login"), url-prefix("http://www.tumblr.com/new/"), url-prefix("http://www.tumblr.com/reblog/"), url-prefix("http://www.tumblr.com/blog/"), url-prefix("http://www.tumblr.com/dashboard/") , url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/new/blog"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/reblog/"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/dashboard/") {
/*BIG BACKGROUND begin - background for the entire screen*/
 body {
   background:url("http://i1258.photobucket.com/albums/ii527/roastings/ooo.jpg~original") no-repeat fixed !important;
   background-color: rgba(106,1,106, 0.95) !important;
   background-position: 0% 0% !important;
   background-size: 100% 100%  !important;
 }
 /*BIG BACKGROUND end*/
/*CONTENT begin - background for posts, icon bar, sidebar*/
 #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
   background: rgba(53,107,9, 0.50) !important;
 }
 /*CONTENT end*/
/*ICON BAR BACKGROUND begin - background for the icon bar*/
 #new_post {
   background-color: rgba(255,255,255, 1.0) !important;
 }
 /*ICON BAR BACKGROUND end*/

.selection_nipple:not([class="selection_nipple white"]) {

  border-bottom: solid 11px rgba(53,107,9,1)  !important;

}
}
