css:
@-moz-document
	url-prefix('https://www.tumblr.com/')
{

#posts .post:after {
  content: '';
  opacity: 0;
/*  -webkit-transition: opacity 1s 2s linear;*/
  transition: opacity .2s .5s linear;
background: url('http://media.tumblr.com/4ab558c1958743d30bdb35971ab3a8d9/tumblr_inline_mmws1bfYtm1qz4rgp.png')!important;
background-size: 120% 120% !important;
position: absolute !important;
width: 61px !important;
height: 70px !important;
top: 25px !important;
right: /*-45*/-62px !important;
}
#posts .post:hover:after {
  opacity: 1;
/*  -webkit-transition: opacity 1s linear;*/
  transition: opacity .5s linear;
}

}
