css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://aaronsw.com/weblog"),
               url-prefix("http://www.aaronsw.com/weblog"),
               url-prefix("https://aaronsw.com/weblog"),
               url-prefix("https://www.aaronsw.com/weblog") {
    body {
        font-size: 100% !important;
    }
    
    h1.fronttitle, h1.title {
        text-align: center !important;
    }
    
    .fronttitle a, .title a {
        color: #000000 !important;
        background-color: transparent !important;
    }
    
    .byline {
        float: none !important;
        margin: 0 auto !important;
        padding: 0 !important;
        text-align: center !important;
    }
    
    .content {
        max-width: 35em !important;
        width: auto !important;
        margin: 2em auto 0 auto !important;
    }
}
