css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

tabmodalprompt {	
background: rgba(0,0,0,.35)  !important;
}
tabmodalprompt .mainContainer {
-moz-appearance: none !important;
background: transparent !important;
border-radius: 5px !important;
min-width:320px!important;
border: 1px solid #666!important;
box-shadow: 0 12px 30px 5px rgba(0,0,0,.55)!important;
color: #333!important;
}
tabmodalprompt .topContainer {
text-shadow: none!important;
font-family:Lucida Grande,sans-serif!important;
color: #222!important;
font-size:13px!important;
background: #ededed!important;
border-radius: 5px 5px 0 0!important;
box-shadow: inset 0 1px 0px rgba(255,255,255,.75)!important;
padding-bottom:10px!important;
}
tabmodalprompt .buttonContainer {
background: #ededed!important;
box-shadow: inset 0 -1px 0px rgba(255,255,255,.95)!important;
border-radius: 0 0 5px 5px!important;
border: 0 !important;
padding-top:5px!important;
}
tabmodalprompt .buttonContainer button {
text-shadow:0px 1px 0px #fff!important;
font-family:Lucida Grande,sans-serif!important;
-moz-appearance: none!important;
color: #434343!important;
border-radius: 4px!important;
border: 1px solid #b5b5b5!important;
background: linear-gradient(#fff, #f2f2f2)!important;
box-shadow: inset 0 1px rgba(255,255,255,.8),
            inset 0 0 1px rgba(255,255,255,.25),
            0 1px rgba(255,255,255,.3)!important;
background-clip: padding-box!important;
background-origin: padding-box!important;
padding: 2px 6px!important;
}
tabmodalprompt .buttonContainer button:hover {
cursor:pointer!important;
}
tabmodalprompt .buttonContainer button:hover:active {
box-shadow: inset 0 1px 4px -3px #000, 0 1px rgba(255, 255, 255, 0.3)!important;
}
tabmodalprompt .topContainer textbox {
color: #000 !important;
background: #fff !important;
font-family:Lucida Grande,sans-serif!important;
margin-top:20px!important;
padding: 3px 2px 2px 3px !important;
}
