@-moz-document domain("smashingmagazine.com") {
::-webkit-scrollbar-thumb,
body::-webkit-scrollbar-thumb,
ol::-webkit-scrollbar-thumb,
ul::-webkit-scrollbar-thumb,
#transcript-scrollbox::-webkit-scrollbar-thumb {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border-radius: 22px!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar-thumb:hover,
body::-webkit-scrollbar-thumb:hover,
ol::-webkit-scrollbar-thumb:hover,
ul::-webkit-scrollbar-thumb:hover,
#transcript-scrollbox::-webkit-scrollbar-thumb:hover {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-track,
#messages div::-webkit-scrollbar-track {
    /* Color of slider */
    background: #170000!important;
    box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar,
#messages div::-webkit-scrollbar {
    /* Width */
    width: .8em!important;
}
 
::-webkit-scrollbar-button,
#messages div::-webkit-scrollbar-button {
    /* Color of handles at top and bottom */
    background: #4c0707!important;
    box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-button:hover,
#messages div::-webkit-scrollbar-button:hover {
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
}

@-moz-document domain("smashingmagazine.com") {
/*_____________________________________________________________________________________*/

* {
	border: none!important;
	border: 0!important;
	box-shadow: none!important;
	outline: none!important;
	text-decoration: none!important;
}

.article--grid__note {
	text-transform: capitalize!important;
}

:focus {
	border: none!important;
	outline: none!important;
}

tr,
.feature-comparison {
    border: 1px solid #f20!important;
}

/*___ Fonts __________________________________________________________________________________*/

.article--post__author-name,
.article__comment,
.article__summary,
.author__desc,
.author__title-desc,
.books__header__title-desc,
.category__title-desc,
.h1,
.h2,
.internal__header__title-desc,
.internal__summary,
.read-more-link,
.search-results__title-desc,
body,
h1,
h2,
header,
html,
time {
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

selector {
	font-size: 15px!important;
	line-height: 27px!important;
}

/*___ Remove Items ___________________________________________________________________________*/

.c-garfield-the-cat>.c-promo-box {
	position: absolute!important;
	top: -9999px!important;
	left: -9999px!important;
}

/*___ Left/Right/Top/Bottom ___________________________________________________________________*/

.main-header {
	margin-bottom: 7rem!important;
}

.featured-articles__teaser {
	margin-top: -6rem!important;
}

.featured-article__tags {
	margin: 3em 0!important;
}

.article--grid__read-more {
	bottom: -36px!important;
}

.article__content .books__book__meta {
	grid-column-end: 1!important;
	grid-row-start: 4!important;
}

.c-garfield-the-cat {
	grid-column-end: 15!important;
}

/*___ Margins _______________________________________________________________________________*/

.article--post,
.col--article--grid,
.col--article--meow__membership.article--grid,
.event__highlights__image-grid img,
.featured-article,
.featured-articles-container,
section {
	margin: 12px!important;
}

.job {
	margin: 12px 0!important;
}

/*___ Paddings ______________________________________________________________________________*/

.article--post,
.article-container .author--full,
.col--article--grid,
.col--article--meow__membership.article--grid,
.featured-article,
.featured-articles-container,
section {
	padding: 12px!important;
}

.vertical span {
	padding: 0 7px!important;
}

/*___ Heights _______________________________________________________________________________*/

selector {
	Max-height: none!important;
}

/*___ Widths ________________________________________________________________________________*/

.article--post__title,
.c-person-of-the-week .author__desc p,
.c-garfield-the-cat,
.container {
	max-width: none!important;
}

.article--post__stats {
	width: auto!important;
}

.col-6 {
	width: 100%!important;
}

.container {
	width: 92%!important;
}

.article--post__content {
	width: 85%!important;
}

.col--article--grid {
	width: 33%!important;
}

.col--article--grid,
.featured-article {
	width: 31.33%!important;
}

.event__highlights__image-grid img {
	width: 30.33%!important;
}
}

@-moz-document domain("smashingmagazine.com") {
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________________________________________*/

td:first-child,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________________________________________*/

.alert small,
.article__content figure figcaption,
.article__image figcaption,
.dashboard-membership__desc.for-guests li:not(.for-guests):active span,
.dashboard-membership__desc.for-guests li:not(.for-guests):focus span,
.dashboard-membership__desc.for-guests li:not(.for-guests):hover span,
.dashboard-membership__desc.for-members li:not(.for-members):active span,
.dashboard-membership__desc.for-members li:not(.for-members):focus span,
.dashboard-membership__desc.for-members li:not(.for-members):hover span,
.dashboard-membership__desc.for-smashing li:not(.for-smashing):active span,
.dashboard-membership__desc.for-smashing li:not(.for-smashing):focus span,
.dashboard-membership__desc.for-smashing li:not(.for-smashing):hover span,
.dashboard-membership__desc.for-supporters li:not(.for-supporters):active span,
.dashboard-membership__desc.for-supporters li:not(.for-supporters):focus span,
.dashboard-membership__desc.for-supporters li:not(.for-supporters):hover span,
.dashboard-membership__desc.for-users li:not(.for-users):active span,
.dashboard-membership__desc.for-users li:not(.for-users):focus span,
.dashboard-membership__desc.for-users li:not(.for-users):hover span,
.feature-comparison tbody td:first-child,
.feature-comparison tbody th:first-child,
.internal__page__wrapper figure figcaption,
.membership__discount small,
.table .tbody .td,
.table tbody td,
body,
li,
p,
table .tbody .td,
table tbody td {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Khaki _______________________________________________________________________________________________*/

:not(pre)>code,
code,
h1,
h2 {
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Burlywood ___________________________________________________________________________________________*/

.author__title-desc,
.books__header__title-desc,
.category__title-desc,
.dashboard .dashboard-membership__callout .dashboard-membership__callout__coming-up li strong,
.internal__header__title-desc,
.search-results__title-desc,
h3,
h4,
td:last-child {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Sandy Brown _________________________________________________________________________________________*/

.article--grid__note,
.grey,
h5,
h6 {
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue ________________________________________________________________________________________________*/

.jobs__list .job__location,
time {
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green _______________________________________________________________________________________________*/

.book-cta__price,
.membership__price {
	color: #0f0!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red _________________________________________________________________________________________________*/

selector {
	color: #f00!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________________________________________*/

.a-input-text,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*________________________________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre::selection {
	background: #ff9!important;
	color: #fff!important;
}

.article__comments--notes span {
	color: #000!important;
	text-shadow: 1px 1px #000!important;
}

.job__meta,
.vertical span {
	color: #efa7a7!important;
	text-shadow: 1px 1px #000!important;
}
}

@-moz-document domain("smashingmagazine.com") {
/*___________________________________________________________________________________________*/

html,
body {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

/*___ Background Gray __________________________________________________________________________*/

.feature-comparison .feature-comparison__category,
.book-cta__title span {
	background-color: #060505!important;
}

/*___ Background 0000 __________________________________________________________________________*/

.books__book__wrapper,
.books__tabs,
.internal__page__wrapper,
.language-css .token.string,
.style .token.string,
.token.entity,
.token.operator,
.token.url,
footer {
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 ______________________________________________________________________*/


#main,
.alert small,
.article--grid:hover .article--grid__read-more,
.article--grid:not(.col--article--meow):hover::before,
.article--grid__read-more,
.cover,
.featured-articles__teaser span,
.membership__discount small {
	background-color: rgba(0, 0, 0, 0)!important;
}

/*___ Highlight Background ________________________________________________________________________*/

selector:hover {
	border-left-color: #434343!important;
	background: linear-gradient(to right, #383838, #0000)!important;
}

/*___ Faded Background bottom (Usually expand paragraph) _____________________________________*/

selector {
	background: linear-gradient(to bottom, rgba(255, 255, 255, 0), rgba(120, 224, 240, 0.13))!important;
}

/*______________________________________________________________________________________________*/

.article--grid__title a,a,
.conf-panel--blue,
.job:hover {
	background-image: none!important;
}

/*______________________________________________________________________________________________*/

pre,
code {
	background: #111!important;
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
}

/*__ Blue ________________________________________________________________________________________*/

Selector {
	border-radius: 10px!important;
	border: none!important;
	background-image: linear-gradient(to bottom right, #05497F, #000)!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

selector:hover {
	background-image: linear-gradient(to bottom right, #06508F, #000)!important;
	color: #ff0!important;
}

selector:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Red ________________________________________________________________________________________*/

.btn,
.btn--white,
.toolbar-item {
	border-radius: 10px!important;
	border: none!important;
	background-image: linear-gradient(to bottom right, #7f0505, #000)!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

