css:
@-moz-document domain("cyberforum.ru") {
body, table, tbody, tr, td, div, p, span.time, span.kw1, select, input, textarea, em, legend, .re1, .xml, .re2{
  color: silver !important;
  background-color: black !important;
  border-color: gray !important;
  font-family: "Lucida Console", Monaco, monospace !important;
}
div.imagebutton, div.vB_Editor_QR_controls table tbody tr td, div.controlbar table tbody tr td,
div.vB_Editor_QR_controls table, div.controlbar table, div.controlbar, div.vB_Editor_QR_controls{
  background-color: gray !important;  
}
a, a>strong{
  color: green !important;
}
body>form:last-of-type table:last-of-type, div.page>div>table:first-of-type{
  display: none !important;
}
::selection{
  color: black !important;
  background-color: silver !important;
}
}
