css:
@-moz-document url-prefix("https://connect.coramdeoacademy.org/") {
/*Navbar Background*/
div.pri-100-bgc {
	background-color: #AA0001;
}
/*background*/
div#app {
  background-image: -webkit-linear-gradient(top, #FF8484 0%,#FFFFFF 100%);
}
/*Navbar Text*/
a {
color: #a00;;
}
/*Assignment List*/
tbody#assignment-center-assignment-items tr {
  background-color: rgba(255, 255, 255, 0.5);
}
/*Borders*/
div.pri-75-bordercolor {
border-color: #AC4040;
}
/*Content Text*/
span.pri-100-fgc {
color: #aa0000;
}
/*Message Icon*/
i.p3icon-message {
color: #ff0000;
}
div.sec-15-bgc {
background-color: #FFEEEE;
}
div.subnavbar {
background-color: #FFEEEE;
border-color: #ff0000;
}
/*Timeout Window*/
div.modal-header {
background-color: #aa0000;
}
/*Active Navbar Selections*/
a.black-fgc.active {
background-image: -webkit-linear-gradient(top, #FFFFFF 0%,#FFDDDD 100%);
}
li.active a {
background-image: -webkit-linear-gradient(top, #FFFFFF 0%,#FFDDDD 100%);
}
/*UPDATED RIBBON*/
div.ribbon-blue {
color: #FFFFFF !important;
background-image: -webkit-linear-gradient(top,#000000,#920000);
}
}
