css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("absurdopedia.net") {
.toccolours tbody tr + tr
,.navbox tbody tr + tr
{visibility:collapse !important}

.toccolours:hover tbody tr + tr
,.navbox:hover tbody tr + tr
{visibility:visible !important; display:table-row !important}

.NavFrame .NavContent
,h2 span:nth-child(1)
,#toctitle + ul
,.toctoggle
,h3 .editsection
{display:none !important}

.NavFrame:hover .NavContent
,h2:hover span:nth-child(1)
,#toc:hover tbody tr td ul:nth-child(2)
,h3:hover .editsection
{display:block !important}

.navbox tbody tr th span[style="float: right; font-weight: normal; text-align: right; width: 6em;"]
,.NavHead a
{visibility:hidden !important}

s:hover {text-decoration:none !important}
.toc {float:left; margin:5px 5px 5px 0}
.mw-content-ltr {cursor: default}
}
