css:
@-moz-document domain(tumblr.com) {
img[src*="http://i41.tinypic.com/2rgnfco.png"],

.new_post_label_icon {

    opacity: 0 !important;

}







#new_post_label_text,

#new_post_label_photo,

#new_post_label_quote,

#new_post_label_link,

#new_post_label_chat,

#new_post_label_audio,

#new_post_label_video {

  background: rgba(255,255,255, 1.0) url("http://i41.tinypic.com/2rgnfco.png") no-repeat !important;

}




#new_post_label_text {

  background-position: 2px 0px !important;

  border-top-left-radius: 8px !important;

  border-bottom-left-radius: 8px !important;

}

#new_post_label_photo {

  background-position: -73px 0px !important;

}

#new_post_label_quote {

  background-position: -148px 0px !important;

}

#new_post_label_link {

  background-position: -223px 0px !important;

}

#new_post_label_chat {

  background-position: -298px 0px !important;

}

#new_post_label_audio {

  background-position: -373px 0px !important;

}

#new_post_label_video {

  background-position: -448px 0px !important;

  border-top-right-radius: 8px !important;

  border-bottom-right-radius: 8px !important;

}
}
