css:
@-moz-document domain("twitter.com") {
.wrapper {
  width: 90% !important;
}
.profile-card.profile-header {
  display: inline-block;
  float: right;
  top: 55px;
  position: absolute !important;
  right: 15px;
}
.wtf-module, .trends, .site-footer {
  display: none !important;    
}
.dashboard {
  /*display: inline-block !important;
  float: left; */
  width: 100% !important; 
}

.dashboard , .content-main {
  width: auto !important;
  float: none !important;
}

.dashboard .tweet-content .tweet-box {
    width: 98% !important;
    font-family: tahoma !important;
}

.dashboard .mini-profile{
    width:100% !important;
}

.dashboard .media-box {
    height: 93px !important;
   /* width: 332px !important;*/
    overflow: hidden;
    padding: 0px 8px 0px 9px;
    margin: 0 8px;
    background-color: #FEFEFE;
}
.dashboard .profile-nav{
    width:calc(100% - 530px);
    float: left;
    display: inline-block;
}
.dashboard .tweet-box{
    width: auto !important; 
}
.dashboard .enhanced-media-thumbnails {
    width:calc(100% - 530px);
    display: block;    
}
.dashboard .enhanced-media-thumbnails .list-link {
    padding: 8px 12px 7px 12px;
}
.tweet-form.condensed div.rich-editor {
    width: 98% !important;
}
.tweet-text {
    font-family: tahoma;
    font-size: 12px !important;
}
}
