css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("trello.com") {
    .current.markeddown ul li {list-style: disc outside none !important; }
    .current.markeddown ol,
    .current.markeddown ul {margin-left: 20px !important; }
    .current.markeddown ul,
    .current.markeddown ol {margin-bottom: 4px !important; }
    .current.markeddown > ul,
    .current.markeddown > ol {margin-bottom: 16px !important; }    
}
