css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("baike.baidu.com") {

DIV.content-hd,
DIV#side.col-sub
{
    display: none !important;
}

/*View Page*/
DIV#content-wrap,
DIV#content,
DIV.content-ft,
DIV#main-wrap,
DIV.content-bd.main-body,
DIV[id^= "sec-header"],
DIV[id^= "lemmaContent-"]
{
    float: none !important;
    width: auto !important;
    margin-left: auto !important;
    margin-right: auto !important;
    overflow: hidden !important;
}

DIV.mod-list /*Search Page*/
{
    width: 98% !important;
}

tr,
DIV.para, /*View Page*/
DIV.abstract /*Search Page*/
{
    font-family: 'Ubuntu Mono','DejaVu Sans Mono','Lucida Sans Typewriter','Consolas','Monospace','Courier New','Courier','abook','QingYuan' !important;
    font-style: normal !important;
}

/* Keys add Shadow */
H1[class*="title"],
H4.card-title,
SPAN.headline-content,
DIV.bk-editable-lemma-btns,
LI.expand,
/* bold */
b,
strong
{
    font-family: 'Ubuntu Mono','DejaVu Sans Mono','Lucida Sans Typewriter','Consolas','Monospace','Courier New','abook','QingYuan' !important;
    font-weight: normal !important;
    color: RGB(0, 144, 32) !important;
    text-shadow: 0px 0px 0.25em rgb(160, 160, 160) !important;
}

/* link */
a
{
    font-family: 'Ubuntu Mono','DejaVu Sans Mono','Lucida Sans Typewriter','Consolas','Monospace','Courier New','abook','QingYuan' !important;
    font-weight: normal !important;
    text-shadow: 0px 0px 0.25em rgb(180, 180, 180) !important;
}

