css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.neon.de") {


body {
    font: 11px/1.231 arial,helvetica,sans-serif !important;
}

header {
    background: none repeat scroll 0 0 !important;
}

body, #container, header, ul#userNav  {
    background: none repeat scroll 0 0 #eeefe9 !important;
}


.teaser .subhead {
    font-size: 1.2em !important;
    margin-top: 10px !important;
}

.teaser .author {
    font-family: arial,helvetica,sans-serif !important;
    font-size: 1.2em !important;
    margin-bottom: 5px !important;
}

.teaser_image_left {
 width: 310px !important;
 padding: 5px !important;
}

.content_magazine_teaser {
    border-top: 5px solid grey !important;
}

/* Startseite Bildergalerien */

.content_subtitle_teaser, .teaser_imagetiles {
    background: none repeat scroll 0 0 #eeefe9 !important;
    padding: 0px !important;
}

/* Startseite kleine Artikelteaser */

.teaserContentArticle.w1 {
    padding: 0 5px 10px !important;
    width: 145px !important;
}

/* Startseite Magazinteaser */
.content_magazine_teaser li a {
    font-size: 1.2em !important;
}

.teaser_comments p {
    font-family: arial,helvetica,sans-serif !important;
    font-size: 1.1em !important;
    margin: 0 0 5px !important;
}


/* Artikelseite */

.view {
    padding: 0 10px !important;
}

.articleView {
    font-family: georgia,times,serif !important;
    font-size: 1.4em !important;
    line-height: 1.5em !important;
}

.commentText p, .msgReadText p, textarea {
    font-family: georgia,helvetica,sans-serif !important;
    font-size: 100% !important;
}

.commentText {
    font-family: georgia,helvetica,sans-serif !important;
    font-size: 1.25em !important;
}

.profile p {
    font-size: 1.3em !important;
}

}
