css:
@-moz-document url-prefix('http://www.tumblr.com/dashboard'), url-prefix('https://www.tumblr.com/dashboard')   {

.logged_in
    {background: url('http://farm3.staticflickr.com/2690/4392305110_a1f37978e4_b.jpg') no-repeat fixed bottom center !important ;
    background-size: cover !important ;}

.right_column
    {background-color: transparent !important ;}

.post_permalink
    {background-image: url('http://static.tumblr.com/zhomr7r/3y6msfctq/dogear.png') !important ;}

.search_form_row
    {background-color: rgba(0,0,0,0.4) !important ;}

.selection_nipple
    {border-bottom-color: rgba(0,0,0,0.4) !important ;
    top: 4px !important ;}

#auto_pagination_loader
    {background-color: rgba(0,0,0,0.8) !important ;
    background-image: none !important ;}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color: rgba(0,0,0,0.4) !important ;}

#right_column:after
    {display: none !important ;}

}
