css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
.dashboard_options_form, .tag_psa {
	border: 0px solid #284059;
	background-color: rgba(144,150,157,.2);
  }
  
body:not(.mceContentBody) {
 background:url('http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o5_400.png') fixed !important;}
.mceContentBody{ background:none !important; }

#new_post.editing {
  background-color: #fff; 
	background-image:none !important;}

.new_post .title, .new_post .tag_editor {
  background-color: rgba(255,255,255,.8) !important;}


#left_column #posts .notification .avatar {opacity:1.7 !important;}
#left_column #posts .notification .avatar:hover{opacity:1 !important;}

#new_post_notice_container{color:#fff!important}


#posts .notification {
	opacity:0.8 !important;
	background:  #163156 !important;
	border: 1px #000000 solid !important;
	margin: -1px;
	}

#posts .notification .nipple.border, #posts .notification.alt .nipple.border {
	border-right: 7px solid #000000 !important;
}

#posts .notification .nipple, #posts .notification.alt .nipple {
	border-right: 6px solid #163056 !important;
}


#logo > .logo_anchor {
	background-image: url(http://24.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o1_250.png)  !important;}

#logo {

	width: 210px !important;
	height: 49px !important;
}


#return_to_top .return_to_top_icon{
background: url('http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o7_100.png') no-repeat transparent!important}



#popover_blogs .tab_notice:after{display:none; }

div.l-header .tab.iconic > a {
		background-image:url('http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o6_250.png') !important;}

.new_post_label{
font-size:0px !important;
}

.new_post_label_icon{background-image:none !important}

#new_post{
background: #fff url(http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o10_r1_1280.png) center no-repeat !important;
}


.selection_nipple.white,
.selection_nipple {
  border: 0px !important;
background-image:url("http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o2_100.png") !important;

  width:  36px !important;
  height: 48px !important;
  margin-top: -12px !important;
  background-position: 0px 0px !important;
  background-repeat: no-repeat !important;
}

#posts  .notification {
background: rgba(255,255,255,.07) !important;
    }

.notification .notification_sentence a {
color: rgba(255,255,255,.7) !important;
    }

.controls_section li:not(.no_push).selected, .controls_section li:not(.no_push).selected:hover, .controls_section li:not(.no_push):active, .controls_section li:not(.no_push):active:hover{
background-color: rgba(22,29,39,.4) !important;
  }

.pill.notice { color: rgba(22,29,39,1) !important; }

#right_column:after {
  background: none !important;
}

.plexi.show.color, .ui_dialog_lock.opaque {
background: rgba(22,29,39,0.8) !important;
}

#toaster { background-color: rgba(22,29,39,.8); }
}

@-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/likes"), url-prefix("http://www.tumblr.com/tagged"), url-prefix("http://www.tumblr.com/inbox"), url-prefix("http://www.tumblr.com/reblog"), url-prefix("https://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/blog") , url-prefix("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/tagged"), url-prefix("https://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/reblog"), url-prefix("https://www.tumblr.com/blog") {
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(22,29,39,.0) !important;}

.setting{background: #ffffff !important;}

.dashboard_settings div.l-content, .dashboard_tumblelog_settings div.l-content, .main_help #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: #fff !important;}
}

@-moz-document url-prefix("http://www.tumblr.com/dashboard/iframe") , url-prefix("https://www.tumblr.com/dashboard/iframe") {
body:not(.mceContentBody) {
 background:none !important;}
}
