css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
body {
    background-color: rgb(22,22,22) !important;
    color: rgb(166,166,166) !important;
}
#main-header {
    background-color: rgb(36,36,36) !important;
}
nav {
    background-color: rgb(36,36,36) !important;
}
a {
    color: rgb(0,173,238) !important;
}
a:hover {
    color: rgb(200,173,238) !important;
}
a:visited {
    color: rgb(206,70,12) !important;
}
a:visited:hover {
    color: rgb(206,70,200) !important;
}
#related {
    background-color: rgb(36,36,36) !important;
}
#view-code {
    background: transparent !important
}
input, code, textarea {
    color: rgb(200,200,200);
    background: transparent !important;
    -moz-appearance: none !important;
    border-radius: 3px !important;
    border: none !important;
    box-shadow: 0px 1px black inset,
              1px 0px black inset,
              -1px 0px black inset,
              0px -1px black inset,
              0px 2px 3px rgba(0, 0, 0, 0.5) inset,
              0 1px 0 rgba(45,45,45,1) !important;
}
input[type="submit"], input[type="button"] {
    background-color: rgb(50,50,50) !important;
    box-shadow: 0px 1px black inset,
              1px 0px black inset,
              -1px 0px black inset,
              0px -1px black inset,
              0px 2px 3px rgba(0, 0, 0, 0.5) !important;
    color: rgb(200,200,200) !important;
}
input[type="submit"]:hover, input[type="button"]:hover {
    background-color: rgb(50,50,50) !important;
    color: rgb(255,255,255) !important;
}
.errorExplanation {
    background-color: rgb(200,50,50) !important;
}
.notice {
    background-color: rgb(50,200,50) !important;
    color: rgb(0,50,200) !important;
}
}
