css:
@-moz-document domain("readability.com"){
	*{
		color:#586e75 !important;
	}
	
	body, .tools{
		background:#eee8d5 !important;
	}
	
	.tools{
		border-right-style:none !important;
	}
	
	a{
		color:#2aa198 !important;
	}
	
	a:hover{
		background:#fdf6e3 !important;
	}
	
	a:visited{
		color:#b58900 !important;
	}
}
