css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("newrepublic.com") {
	header,
	footer,
	#other,
	#marginalia,
	.share,
	div.secondary,
	#primary-title p,
	#controls
	{display:none !important;}
	div#page, div#main, #primary-title {
		display: block !important;
		float: none !important;
		background: #f9fcf9 !important;
		margin: 0 auto !important;
		width: 100% !important;
		padding: 0  !important;
	}
	.page .main .primary {
		box-shadow: none !important;
		color: #303030 !important;
		background-color: #f9fcf9 !important;
		line-height: 100% !important;
		font-family: 'Linux Libertine', 'Linux Libertine O', serif !important;
		font-size: 18pt !important;
		width: 80% !important;
		margin: 0 auto !important;
		padding: 0 !important;
		display: block !important;
		float: none !important;
	}
	#primary-title h2 {
		margin: 0 auto !important;
		width: 92% !important;
		text-align: center !important;
	}
	.page .main .primary > p {
		line-height: 1.6 !important;
	}
}
