css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ufcpp.net") {
	/* 広告を非表示にし、広告があった空欄を詰めます */
	/* 上部広告非表示（２か所）*/
	div.CommonHeader>ins[style^='display:inline-table;border:none;height:90px;margin:0;padding:0;position:relative;visibility:visible;width:728px']
	{ display: none !important; }

	/* 記事左部広告非表示 */
	div.Ad
	{ display: none !important; }

	/* 記事左部広告部分空欄詰め */
	div.Body
	{ margin: 0 !important; }

	/* 下部広告非表示 */
	div.Footer>div.CommonHeader
	{ display: none !important; }

	/* メニュー上部空欄詰め */
	div.MenuList
	{ top: 0px !important; }

	/* 広告非表示および空欄詰め終了 */

	/* サンプルソースインデント修正 */
	pre.source { margin: 0 0 0 1.6em; }

	/* 右メニューの左メニュー化 */
	div.MenuList{ right: auto !important; }
	div.Main { left: 180px !important; right: auto !important; }
}
