css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("github.com") {
.blob-line-nums span {
    height: 17px;
}
.line {
    line-height: 17px !important;
}
.highlight pre {
    font-family: "Ubuntu Mono Bold","Source Code Pro","MS Gothic",monospace !important;
    background-color: black !important;
}
.highlight pre, .highlight pre * {
    color: white !important;
    font-weight: bold !important;
    font-style: normal !important;
}
.highlight pre .k { color: #77f !important; }
.highlight pre .kc { color: #77f !important; }
.highlight pre .kd { color: #77f !important; }
.highlight pre .kt { color: #77f !important; }
.highlight pre .kn { color: #77f !important; }
.highlight pre .c1 { color: #777 !important; }
.highlight pre .c2 { color: #777 !important; }
.highlight pre .cm { color: #777 !important; }
.highlight pre .s1 { color: red !important; }
.highlight pre .s2 { color: red !important; }
.highlight pre .ss { color: red !important; }
.highlight pre .sr { color: red !important; }
.highlight pre .sh { color: red !important; }
.highlight pre .sx { color: red !important; }
.highlight pre .s { color: red !important; }
.highlight pre .nd { color: red !important; }
}
