@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("db.de") {
  p, img, #bar, table, h1, #stopper, #legalInfoBox, #whiteBar, .tls, #strtImg, #content, #copyrightBox, .footerspacer, .logo {
    display: none;
  }
  
  #maincontainer {
    height: auto !important;
    width: 190px;
    margin: 100px auto 0 auto;
  }
  
  form > h1 {
    display: block;
  }

  .appTea {
    display: block;
  }
}
