css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("bluetouff.com") {

@media print {
    .wp-flattr-button, .commentlist .avatar, .commentlist .reply {
        display: none !important;
    }
    .commentlist cite {
        font-style: normal;
        font-weight: bold !important;
    }
    #content, .post_container, .container, .row, .eight, .columns {
        margin: 0 !important;
        padding: 0 !important;
        float: none !important;
        width: auto !important;
        max-width: 100% !important; min-width: auto !important;
    }
}

header,
#crumbs,
.postformats,
.tags, .prev-posts-single, .next-posts-single,
#postbar,
#sidebar, #respond,
#footer, #afterfooter,
div.entry > *:nth-last-child(-n+1)
{
    display: none !important;
}

img {
    display: inline !important;
    max-width: 98% !important;
}

}
