css:
@-moz-document domain("youtube.com") {

#player {
    padding-left: 0 !important;
}

#player-api {
    width: 100% !important;
    max-width: 1920px !important;
    margin: auto !important;
    height: 600px !important;
}

.watch #guide {
    top: 600px !important;
}

#watch7-sidebar {
    margin-top: 0px !important;
    padding-top: 10px !important;
}

/* Disables playlists */

#playlist,
.watch7-playlist-bar-left {
    width: 100% !important;
}

.watch7-playlist-bar-right {
    -moz-transition: opacity .3s ease-in !important;
    opacity: 0 !important;
    left: -400px !important;
    width: 400px !important;
}

.watch7-playlist-bar-left:hover + .watch7-playlist-bar-right,
.watch7-playlist-bar-right:hover {
    opacity: 1 !important;
}


/* 
 *
 * Youtube - Lights off! fix
 *
 * Lights off script has to be enabled before you enable this script!
 * Otherwise it won't work.
 *
 */
 
.exp-new-site-width #page {
    width: auto !important;
}

#yt-masthead-container {
    padding-bottom: 0px  !important;
    padding-top: 0px  !important;
}

#page.watch {
    padding-top: 0px !important;
}

}
