css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("cduniverse.com"),
domain("ixquick-proxy.com")
{
html, head, body, input, select
{
background:#000000 !important;
}
#mastertable, #nb,
#ntgeneric tr td,
.bordergray tr td
{
background-image:none !important;
background-color:#6D1714 !important;
}
#chunky tr td, #dfci3 tr td,
.borderdgLRB_curveLbRb, .borderdgLlgtR,
#superchunky tbody tr td
{
background-color:#111111 !important;
}
#footertable tbody tr td
{
background:#111111 !important;
}
input, select
{
border:1px #000000 solid !important;
}
h1, h2, h3, h4, h5, div, span, nobr
{
color:#FFFFFF !important;
}
a, input, select
{
color:#FFFFFF !important;
text-decoration:underline!important;
}
a:hover
{
color:#FFFF00 !important;
text-decoration:underline!important;
}
}
