css:
@-moz-document domain("blabler.pl") {
/* this is ugly, there be dragons */

body
{
    background: #222;
    color: #ccc;
}
#dashboardHeader
{
    color: rgba(238, 170, 0, 0.7);
}
a[target="_blank"]
{
    color: #fc4 !important;
}
#content a,
#content a:link,
#content a:visited,
#footer a,
#footer a:link,
#footer a:visited
{
    color: #adf;
}
a[href^="/tag/"]
{
    color: #dfff00!important;
}
.permalink a:hover
{
    color: #adf!important;
}
#posts > div,
#content > div
{
    color: silver!important;
    border: 1px solid #555!important;
    background: linear-gradient(to bottom, #222, #111)!important;
}
#content .status
{
    background: #111;
    border-color: #444;
    box-shadow: 1px 1px 3px black;
    color: #d0d8df;
}
.postImage
{
    border: 1px solid #222;
    box-shadow: 2px 2px 2px #000;
}
h2,
.frontHeader
{
    color: #fff;
}
div table tbody br+div[style*="background"],
#content > table > tbody > tr > td:nth-child(1) > div:nth-child(3)
{
    border-color: #000 !important;
    box-shadow: 0 0 4px #000;
    background: linear-gradient(to bottom, #181818, #2a2a2a)!important;
}
textarea
{
    color: #eee;
    background: linear-gradient(to bottom, #000, #222)!important;
    box-shadow: inset 0 0 2px #6cf;
}
div.jquery-filestyle label
{
    color: #8cf;
    background: #333;
    border: 1px solid #555;
}
div.jquery-filestyle label:hover
{
    color: #ff8600;
    background: #484848;
    border: 1px solid #000;
}
div.jquery-filestyle input
{
    background: transparent !important;
}
input.blabler[type="submit"]
{
    color: #8cf;
    border: 1px solid #666 !important;
}
input,
button,
.blabler
{
    background: linear-gradient(to top, #000, #222)!important;
    color: #fff;
    border: 1px solid #666;
}
.avatar
{
    background-color: #444;
}
menu #content
{
    border-bottom: none;
}
#footer
{
    border-color: #666;
    background: #282828;
}
}
